import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../assets/styles/Login2.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      // Check if response is OK before parsing
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || 'Login failed');
      }

      const data = await response.json();
      
      // Successful login
      setMessage(data.message);
      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));
      
      // Navigate to dashboard or home
      navigate('/dashboard');

    } catch (error) {
      console.error('Login error:', error);
      setMessage(error.message || 'Network error');
    }
  };


  return (
    <div className="login-wrapper">
      <div className="wrapper">
        <div className="login-box">
          <form onSubmit={handleLogin}>
            <h1>Login</h1>
            <div className="input-box">
              <img src="/Images/email_logo.png" alt="Email Logo" className="email-logo" />
              <input 
                type="email"
                placeholder="Email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <i className='bx bxs-user'></i>
            </div>
            <div className="input-box">
              <img 
                src={showPassword ? "/Images/lock-open_logo.png" : "/Images/password_logo.png"}
                alt="Password Logo"
                className="password-logo"
                onClick={toggleShowPassword}
                style={{ cursor: 'pointer' }}
              />
              <input 
                type={showPassword ? 'text' : 'password'}
                placeholder="Password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <i className='bx bxs-lock'></i>
            </div>
            <div className="remember-forgot">
              <label><input type="checkbox" /> Remember me</label>
              <a href="#">Forgot password?</a>
            </div>
            <button type="submit" className="btn">Login</button>
            <div className="register-link">
              <p>Don't have an account? <a href="/register">Register</a></p>
            </div>
            {message && <p>{message}</p>}
          </form>
          <button className="btn home-btn" onClick={() => navigate('/')}>
            <img src="\Images\home_logo.png" alt="Home Logo" className="home-logo" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Login;
