import React, { useState, useEffect } from 'react'; 
import { useNavigate } from 'react-router-dom'; 
import axios from 'axios'; 
import '../assets/styles/Login2.css';  

function Login() { 
  const [email, setEmail] = useState('');   
  const [password, setPassword] = useState('');   
  const [message, setMessage] = useState('');   
  const [showPassword, setShowPassword] = useState(false);
  const [userProfile, setUserProfile] = useState(null);
  const navigate = useNavigate();    

  const handleLogin = async (e) => {     
    e.preventDefault();     
    try {       
      const response = await axios.post('/api/auth/login', { email, password });       
      setMessage(response.data.message);       
      
      // Store the token in local storage
      localStorage.setItem('token', response.data.token);       
      
      // Fetch user profile
      const profileResponse = await axios.get('/api/auth/profile', {
        headers: { 
          'Authorization': `Bearer ${response.data.token}` 
        }
      });
      
      setUserProfile(profileResponse.data);
      
      // Navigate to the dashboard or another protected route       
      navigate('/dashboard');     
    } catch (error) {       
      setMessage(error.response.data.message);     
    }   
  };    

  // Fetch user profile if token exists
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchUserProfile(token);
    }
  }, []);

  const fetchUserProfile = async (token) => {
    try {
      const response = await axios.get('/api/auth/profile', {
        headers: { 
          'Authorization': `Bearer ${token}` 
        }
      });
      setUserProfile(response.data);
    } catch (error) {
      console.error('Failed to fetch user profile', error);
      localStorage.removeItem('token');
    }
  };

  const toggleShowPassword = () => {     
    setShowPassword(!showPassword);   
  };    

  return (     
    <div className="login-wrapper">       
      <div className="wrapper">         
        <div className="login-box">           
          <form onSubmit={handleLogin}>             
            <h1>Login</h1>             
            <div className="input-box">               
              <img src="/Images/email_logo.png" alt="Email Logo" className="email-logo" />               
              <input                  
                type="email"                  
                placeholder="Email"                  
                required                  
                value={email}                  
                onChange={(e) => setEmail(e.target.value)}                
              />               
              <i className='bx bxs-user'></i>             
            </div>              
            <div className="input-box">               
              <img                 
                src={showPassword ? "/Images/lock-open_logo.png" : "/Images/password_logo.png"}                 
                alt="Password Logo"                 
                className="password-logo"                 
                onClick={toggleShowPassword}                 
                style={{ cursor: 'pointer' }}               
              />               
              <input                  
                type={showPassword ? 'text' : 'password'}                 
                placeholder="Password"                  
                required                  
                value={password}                  
                onChange={(e) => setPassword(e.target.value)}                
              />               
              <i className='bx bxs-lock'></i>             
            </div>              
            <div className="remember-forgot">               
              <label><input type="checkbox" /> Remember me</label>               
              <a href="#">Forgot password?</a>             
            </div>             
            <button type="submit" className="btn">Login</button>              
            <div className="register-link">               
              <p>Don't have an account? <a href="/register">Register</a></p>             
            </div>             
            {message && <p>{message}</p>}
            {userProfile && (
              <div className="user-profile">
                <h2>Welcome, {userProfile.firstName} {userProfile.lastName}</h2>
                <p>Email: {userProfile.email}</p>
              </div>
            )}
          </form>           
          <button className="btn home-btn" onClick={() => navigate('/')}>           
            <img src="\Images\home_logo.png" alt="Home Logo" className="home-logo" />           
          </button>         
        </div>       
      </div>     
    </div>   
  ); 
}  

export default Login;
