import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../assets/styles/Login2.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/auth/login', { email, password });
      setMessage(response.data.message);
      // Store the token in local storage or state management
      localStorage.setItem('token', response.data.token);
      // Navigate to the dashboard or another protected route
      navigate('/dashboard');
    } catch (error) {
      setMessage(error.response.data.message);
    }
  };

  const toggleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className="login-wrapper">
      <div className="wrapper">
        <div className="login-box">
          <form onSubmit={handleLogin}>
            <h1>Login</h1>
            <div className="input-box">
              <img src="/Images/email_logo.png" alt="Email Logo" className="email-logo" />
              <input
                type="email"
                placeholder="Email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <i className='bx bxs-user'></i>
            </div>

            <div className="input-box">
              <img
                src={showPassword ? "/Images/lock-open_password.png" : "/Images/password_logo.png"}
                alt="Password Logo"
                className="password-logo"
                onClick={toggleShowPassword}
                style={{ cursor: 'pointer' }}
              />
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="Password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <i className='bx bxs-lock'></i>
            </div>

            <div className="remember-forgot">
              <label><input type="checkbox" /> Remember me</label>
            </div>

            <button type="submit">Login</button>
            {message && <p>{message}</p>}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
