import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../assets/styles/Login2.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'email') setEmail(value);
    if (name === 'password') setPassword(value);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/auth/login', { email, password });
      console.log(response.data);
      navigate('/dashboard'); // Redirect to dashboard or another page after successful login
    } catch (error) {
      console.error(error);
      setMessage(`Error: ${error.response ? error.response.data.message : error.message}`);
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <form onSubmit={handleFormSubmit}>
          <div className="input-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              required
              value={email}
              onChange={handleInputChange}
            />
            <i className='bx bxs-envelope'></i>
          </div>
          <div className="input-group">
            <input
              type="password"
              name="password"
              placeholder="Password"
              required
              value={password}
              onChange={handleInputChange}
            />
            <i className='bx bxs-lock'></i>
          </div>
          <div className="remember-forgot">
            <label><input type="checkbox" /> Remember me</label>
          </div>
          <button type="submit" className="btn">Login</button>
          <div className="register-link">
            <p>Don't have an account? <a href="/register">Register</a></p>
          </div>
          {message && <p className="message">{message}</p>}
        </form>
        <button className="btn home-btn" onClick={() => navigate('/')}>
          <img src="\Images\home_logo.png" alt="Home Logo" className="home-logo" />
        </button>
      </div>
    </div>
  );
}

export default Login;
