//Register.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import '../assets/styles/Register.css';

function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage('Passwords do not match');
      return;
    }
    try {
      const response = await axios.post('/api/auth/register', {
        username,
        password,
        firstName,
        lastName,
        phoneNumber,
        dateOfBirth,
        email,
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response.data.message);
    }
  };

  const toggleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const toggleShowConfirmPassword = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/users/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      // Handle the fetched user profile data
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching profile', error);
    }
  };

  // Add a method to update user profile (PUT method)
  const updateUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put('/api/users/profile', {
        firstName,
        lastName,
        phoneNumber,
        email
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response.data.message);
    }
  };

  // Optionally call fetchUserProfile when component mounts
  useEffect(() => {
    fetchUserProfile();
  }, []);

  return (
    <div className="register-wrapper">
      <div className="wrapper">
        <div className="register-box">
          <form onSubmit={handleRegister}>
            <h1>Sign up</h1>
            <div className="input-box">
              <input 
                type="text" 
                placeholder="First Name" 
                required 
                value={firstName} 
                onChange={(e) => setFirstName(e.target.value)} 
              />
              <i className='bx bxs-user'></i>
            </div>
            <div className="input-box">
              <input 
                type="text" 
                placeholder="Last Name" 
                required 
                value={lastName} 
                onChange={(e) => setLastName(e.target.value)} 
              />
              <i className='bx bxs-user'></i>
            </div>
            <div className="input-box">
            <img src="\Images\phone_logo.png" alt="Phone Logo" className="phone-logo" />
              <input 
                type="text" 
                placeholder="Phone Number" 
                required 
                value={phoneNumber} 
                onChange={(e) => setPhoneNumber(e.target.value)} 
              />
              <i className='bx bxs-phone'></i>
            </div>
            <div className="input-box">
              <input 
                type="date" 
                placeholder="Date of Birth" 
                required 
                value={dateOfBirth} 
                onChange={(e) => setDateOfBirth(e.target.value)} 
              />
              <i className='bx bxs-calendar'></i>
            </div>
            <div className="input-box">
            <img src="\Images\email_logo.png" alt="Email Logo" className="email-logo" />
              <input 
                type="email" 
                placeholder="Email" 
                required 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
              />
              <i className='bx bxs-envelope'></i>
            </div>
            <div className="input-box">
            <img src="\Images\user_logo.png" alt="User Logo" className="user-logo" />
              <input 
                type="text" 
                placeholder="Username" 
                required 
                value={username} 
                onChange={(e) => setUsername(e.target.value)} 
              />
              <i className='bx bxs-user'></i>
            </div>
            <div className="input-box">
            <img src="\Images\password_logo.png" alt="Password Logo" className="password-logo" onClick={toggleShowPassword} style={{ cursor: 'pointer' }} />
              <input 
                type={showPassword ? 'text' : 'password'}
                placeholder="Password" 
                required 
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
              />
              <i className='bx bxs-lock'></i>
            </div>
            <div className="input-box">
              <img src={showConfirmPassword ? "/Images/lock-open_password.png" : "/Images/password_logo.png"} alt="Password Logo" className="password-logo" onClick={toggleShowConfirmPassword} style={{ cursor: 'pointer' }} />
              <input 
                type={showConfirmPassword ? 'text' : 'password'}
                placeholder="Confirm Password" 
                required 
                value={confirmPassword} 
                onChange={(e) => setConfirmPassword(e.target.value)} 
              />
              <i className='bx bxs-lock'></i>
            </div>
            <button type="submit" className="btn">Sign up</button>

            <div className="register-link">
              <p>Already have an account? <Link to="/login">Login</Link></p>
            </div>
            {message && <p>{message}</p>}
          </form>
          <button className="btn home-btn" onClick={() => navigate('/')}>
          <img src="\Images\home_logo.png" alt="Home Logo" className="home-logo" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Register;
