import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../assets/styles/Login2.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/auth/login', { email, password });
      setMessage(response.data.message);
      // Store the token in local storage or state management
      localStorage.setItem('token', response.data.token);
      // Navigate to the dashboard or another protected route
      navigate('/dashboard');
    } catch (error) {
      setMessage(error.response.data.message);
    }
  };

  return (
    <div className="login-wrapper">
      <div className="wrapper">
        <div className="login-box">
          <form onSubmit={handleLogin}>
            <h1>Login</h1>
            <div className="input-box">
              <img src="\Images\user_logo.png" alt="User Logo" className="user-logo" />
              <input 
                type="email" 
                placeholder="Email" 
                required 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
              />
              <i className='bx bxs-user'></i>
            </div>

            <div className="input-box">
            <img src="\Images\password_logo.png" alt="Password Logo" className="password-logo" />
              <input 
                type="password" 
                placeholder="Password" 
                required 
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
              />
              <i className='bx bxs-lock'></i>
            </div>

            <div className="remember-forgot">
              <label><input type="checkbox" /> Remember me</label>
              <a href="#">Forgot password?</a>
            </div>
            <button type="submit" className="btn">Login</button>

            <div className="register-link">
              <p>Don't have an account? <a href="/register">Register</a></p>
            </div>
            {message && <p>{message}</p>}
          </form>
          <button className="btn home-btn" onClick={() => navigate('/')}>
          <img src="\Images\home_logo.png" alt="Home Logo" className="home-logo" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Login;
