import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom'; 
import '../assets/styles/Login2.css';  

function Login() {   
  const [email, setEmail] = useState('');   
  const [password, setPassword] = useState('');   
  const [message, setMessage] = useState('');   
  const [showPassword, setShowPassword] = useState(false);   
  const navigate = useNavigate();    

  const handleLogin = async (e) => {     
    e.preventDefault();     
    try {       
      const response = await fetch('http://localhost:3000/api/auth/login', {         
        method: 'POST',         
        headers: {           
          'Content-Type': 'application/json'         
        },
        body: JSON.stringify({ email, password })       
      });          

      // Parse the response
      const data = await response.json();
      
      // Check if the response is successful
      if (response.ok) {           
        // Store the token in localStorage
        localStorage.setItem('token', data.token);
        
        // Set success message
        setMessage(data.message || 'Login successful');
        
        // Navigate to dashboard or home page
        navigate('/dashboard');         
      } else {           
        // Set error message from server
        setMessage(data.message || 'Login failed');         
      }     
    } catch (error) {       
      console.error('Login error:', error);       
      setMessage(`Error: ${error.message}`);     
    }   
  };    

  const toggleShowPassword = () => {     
    setShowPassword(!showPassword);   
  };    

  return (     
    <div className="login-wrapper">       
      <div className="wrapper">         
        <div className="login-box">           
          <form onSubmit={handleLogin}>             
            <h1>Login</h1>             
            <div className="input-box">               
              <img src="/Images/email_logo.png" alt="Email Logo" className="email-logo" />               
              <input                  
                type="email"                 
                placeholder="Email"                 
                required                 
                value={email}                 
                onChange={(e) => setEmail(e.target.value)}               
              />               
              <i className='bx bxs-user'></i>             
            </div>             
            <div className="input-box">               
              <img                  
                src={showPassword ? "/Images/lock-open_logo.png" : "/Images/password_logo.png"}                 
                alt="Password Logo"                 
                className="password-logo"                 
                onClick={toggleShowPassword}                 
                style={{ cursor: 'pointer' }}               
              />               
              <input                  
                type={showPassword ? 'text' : 'password'}                 
                placeholder="Password"                 
                required                 
                value={password}                 
                onChange={(e) => setPassword(e.target.value)}               
              />               
              <i className='bx bxs-lock'></i>             
            </div>             
            <div className="remember-forgot">               
              <label><input type="checkbox" /> Remember me</label>               
              <a href="#">Forgot password?</a>             
            </div>             
            <button type="submit" className="btn">Login</button>             
            <div className="register-link">               
              <p>Don't have an account? <a href="/register">Register</a></p>             
            </div>             
            {message && <p className="message">{message}</p>}           
          </form>           
          <button className="btn home-btn" onClick={() => navigate('/')}>             
            <img src="\Images\home_logo.png" alt="Home Logo" className="home-logo" />           
          </button>         
        </div>       
      </div>     
    </div>   
  ); 
}  

export default Login;
