//Register.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import '../assets/styles/Register.css';

function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const navigate = useNavigate();

  // In Register.js
const handleRegister = async (e) => {
  e.preventDefault();
  if (password !== confirmPassword) {
    setMessage('Passwords do not match');
    return;
  }
  
  try {
    const response = await fetch('http://localhost:3000/api/auth/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        username,
        password,
        firstName,
        lastName,
        phoneNumber,
        dateOfBirth,
        email,
      })
    });

    const data = await response.json();

    if (response.ok) {
      setMessage(data.message);
      // Optionally navigate to login or dashboard
    } else {
      setMessage(data.message || 'Registration failed');
    }
  } catch (error) {
    setMessage(`Error: ${error.message}`);
  }
};

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put('http://localhost:3000/register', {
        username,
        password,
        firstName,
        lastName,
        phoneNumber,
        dateOfBirth,
        email,
      });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response.data.message);
    }
  };

  const handleGetUser = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`http://localhost:3000/register${email}`);
      const user = response.data;
      setUsername(user.username);
      setFirstName(user.firstName);
      setLastName(user.lastName);
      setPhoneNumber(user.phoneNumber);
      setDateOfBirth(user.dateOfBirth);
      setMessage('User data retrieved successfully');
    } catch (error) {
      setMessage(error.response.data.message);
    }
  };

  const toggleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const toggleShowConfirmPassword = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  return (
    <div className="register-wrapper">
      <div className="wrapper">
        <div className="register-box">
          <form onSubmit={handleRegister}>
            <h1>Register</h1>
            <div className="input-box">
              <img src="/Images/email_logo.png" alt="Email Logo" className="email-logo" />
              <input
                type="email"
                placeholder="Email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <i className='bx bxs-user'></i>
            </div>
            <div className="input-box">
              <img
                src={showPassword ? "/Images/lock-open_logo.png" : "/Images/password_logo.png"}
                alt="Password Logo"
                className="password-logo"
                onClick={toggleShowPassword}
                style={{ cursor: 'pointer' }}
              />
              <input
                type={showPassword ? 'text' : 'password'}
                placeholder="Password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <i className='bx bxs-lock'></i>
            </div>
            <div className="input-box">
              <img
                src={showConfirmPassword ? "/Images/lock-open_logo.png" : "/Images/password_logo.png"}
                alt="Password Logo"
                className="password-logo"
                onClick={toggleShowConfirmPassword}
                style={{ cursor: 'pointer' }}
              />
              <input
                type={showConfirmPassword ? 'text' : 'password'}
                placeholder="Confirm Password"
                required
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
              <i className='bx bxs-lock'></i>
            </div>
            <div className="input-box">
              <input
                type="text"
                placeholder="Username"
                required
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <i className='bx bxs-user'></i>
            </div>
            <div className="input-box">
              <input
                type="text"
                placeholder="First Name"
                required
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
              />
              <i className='bx bxs-user'></i>
            </div>
            <div className="input-box">
              <input
                type="text"
                placeholder="Last Name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
              />
              <i className='bx bxs-user'></i>
            </div>
            <div className="input-box">
              <input
                type="text"
                placeholder="Phone Number"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
              />
              <i className='bx bxs-phone'></i>
            </div>
            <div className="input-box">
              <input
                type="date"
                placeholder="Date of Birth"
                required
                value={dateOfBirth}
                onChange={(e) => setDateOfBirth(e.target.value)}
              />
              <i className='bx bxs-calendar'></i>
            </div>
            <button type="submit">Register</button>
            <button type="button" onClick={handleUpdate}>Update</button>
            <button type="button" onClick={handleGetUser}>Get User</button>
            {message && <p>{message}</p>}
          </form>
          <div className="register-link">
            <p>Already have an account? <Link to="/login">Login</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Register;
