import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // Import the custom CSS file
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { Navbar, Nav, Container } from 'react-bootstrap';
import Home from './Home'; // Assuming you have a Home.js component
import Login2 from './Login2'; // Assuming you already have Login2.js implemented

function App() {
  const location = useLocation();

  return (
    <div className="App">
      {location.pathname !== '/login' && (
        <Navbar className="navbar-custom fixed-top" expand="lg">
          <Container>
            <Navbar.Brand href="#home" className="logo">SAG AWARDS</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto">
                <Nav.Link href="#home">Home</Nav.Link>
              </Nav>
              <Nav className="ms-auto">
                <Nav.Link href="/login">Login/Register</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      )}

      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login2 />} />
      </Routes>
    </div>
  );
}

function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}

export default AppWrapper;
