import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Login2.css';
import Photo from './Photo.jpg';

const mockUsers = [
  { username: 'user1', password: 'password1' },
  { username: 'user2', password: 'password2' },
];

function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleRegister = (e) => {
    e.preventDefault();
    const userExists = mockUsers.some((user) => user.username === username && user.password === password);
    if (userExists) {
      setMessage('You already have an account');
    } else {
      mockUsers.push({ username, password });
      setMessage('Registered successfully');
    }
  };

  return (
    <div className="login-wrapper" style={{ backgroundImage: `url(${Photo})`, backgroundSize: 'cover', backgroundPosition: 'center', height: '100vh' }}>
      <div className="form-container">
        <form onSubmit={handleRegister}>
          <h1>Sign up</h1>
          <div className="input-box">
            <input type="text" placeholder="Username" required value={username} onChange={(e) => setUsername(e.target.value)} />
            <i className='bx bxs-user'></i>
          </div>

          <div className="input-box">
            <input type="password" placeholder="Password" required value={password} onChange={(e) => setPassword(e.target.value)} />
            <i className='bx bxs-lock-alt'></i>
          </div>

          <button type="submit" className="btn">Sign up</button>

          <div className="register-link">
            <p>Already have an account? <Link to="/login">Login</Link></p>
          </div>
          {message && <p>{message}</p>}
        </form>
        <button className="btn" onClick={() => navigate('/')}>Home</button>
      </div>
    </div>
  );
}

export default Register;
