//Login2.js
import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom'; 
import '../assets/styles/Login2.css';  

function Login() {   
  const [email, setEmail] = useState('');   
  const [password, setPassword] = useState('');   
  const [message, setMessage] = useState('');   
  const [showPassword, setShowPassword] = useState(false);   
  const navigate = useNavigate(); 
     
  const handleLogin = async (e) => {     
    e.preventDefault();     
    try {       
      const response = await fetch('http://localhost:3000/api/auth/login', {  // Note the /api/auth prefix
        method: 'POST',         
        headers: {           
          'Content-Type': 'application/json'         
        },
        body: JSON.stringify({ email, password })       
      });          
  
      // Log the raw response before parsing
      console.log('Raw response:', response);
  
      // Check if the response is not OK before parsing
      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error response:', errorText);
        throw new Error(errorText);
      }
  
      const data = await response.json();
      
      // Rest of your existing login logic...
    } catch (error) {       
      console.error('Login error:', error);       
      setMessage(`Error: ${error.message}`);     
    }   
  };
  const toggleShowPassword = () => {     
    setShowPassword(!showPassword);   
  };    

  return (     
    <div className="login-wrapper">       
      <div className="wrapper">         
        <div className="login-box">           
          <form onSubmit={handleLogin}>             
            <h1>Login</h1>             
            <div className="input-box">               
              <img src="/Images/email_logo.png" alt="Email Logo" className="email-logo" />               
              <input                  
                type="email"                 
                placeholder="Email"                 
                required                 
                value={email}                 
                onChange={(e) => setEmail(e.target.value)}               
              />               
              <i className='bx bxs-user'></i>             
            </div>             
            <div className="input-box">               
              <img                  
                src={showPassword ? "/Images/lock-open_logo.png" : "/Images/password_logo.png"}                 
                alt="Password Logo"                 
                className="password-logo"                 
                onClick={toggleShowPassword}                 
                style={{ cursor: 'pointer' }}               
              />               
              <input                  
                type={showPassword ? 'text' : 'password'}                 
                placeholder="Password"                 
                required                 
                value={password}                 
                onChange={(e) => setPassword(e.target.value)}               
              />               
              <i className='bx bxs-lock'></i>             
            </div>             
            <div className="remember-forgot">               
              <label><input type="checkbox" /> Remember me</label>             
            </div>             
            <button type="submit" className="btn">Login</button>             
            <div className="register-link">               
              <p>Don't have an account? <a href="/register">Register</a></p>             
            </div>             
            {message && <p className="message">{message}</p>}           
          </form>           
          <button className="btn home-btn" onClick={() => navigate('/')}>             
            <img src="\Images\home_logo.png" alt="Home Logo" className="home-logo" />           
          </button>         
        </div>       
      </div>     
    </div>   
  ); 
}  

export default Login;
