import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case 'username':
        setUsername(value);
        break;
      case 'password':
        setPassword(value);
        break;
      case 'confirmPassword':
        setConfirmPassword(value);
        break;
      case 'firstName':
        setFirstName(value);
        break;
      case 'lastName':
        setLastName(value);
        break;
      case 'phoneNumber':
        setPhoneNumber(value);
        break;
      case 'dateOfBirth':
        setDateOfBirth(value);
        break;
      case 'email':
        setEmail(value);
        break;
      default:
        break;
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('http://localhost:3000/api/auth/register', {
        username,
        password,
        firstName,
        lastName,
        phoneNumber,
        dateOfBirth,
        email
      });
      setMessage('User registered successfully');
      console.log(response.data);
      navigate('/login'); // Redirect to login page after successful registration
    } catch (error) {
      console.error(error);
      setMessage('Error registering user');
    }
  };

  return (
    <form onSubmit={handleRegister}>
      <input type="text" name="username" value={username} onChange={handleInputChange} placeholder="Username" required />
      <input type="password" name="password" value={password} onChange={handleInputChange} placeholder="Password" required />
      <input type="password" name="confirmPassword" value={confirmPassword} onChange={handleInputChange} placeholder="Confirm Password" required />
      <input type="text" name="firstName" value={firstName} onChange={handleInputChange} placeholder="First Name" required />
      <input type="text" name="lastName" value={lastName} onChange={handleInputChange} placeholder="Last Name" required />
      <input type="text" name="phoneNumber" value={phoneNumber} onChange={handleInputChange} placeholder="Phone Number" required />
      <input type="date" name="dateOfBirth" value={dateOfBirth} onChange={handleInputChange} placeholder="Date of Birth" required />
      <input type="email" name="email" value={email} onChange={handleInputChange} placeholder="Email" required />
      <button type="submit">Register</button>
      {message && <p>{message}</p>}
    </form>
  );
};

export default Register;
