// Home.js
import React, { useState, useEffect } from 'react';

function Home() {
  const [currentSlide, setCurrentSlide] = useState(0);
  
  const slides = [
    '/Images/image1.jpg',
    '/Images/image2.jpg',
    '/Images/image3.jpg',
    '/Images/image4.jpg',
    '/Images/image5.jpg'
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prevSlide) => 
        prevSlide === slides.length - 1 ? 0 : prevSlide + 1
      );
    }, 3000);

    return () => clearInterval(timer);
  }, []);

  const nextSlide = () => {
    setCurrentSlide(currentSlide === slides.length - 1 ? 0 : currentSlide + 1);
  };

  const prevSlide = () => {
    setCurrentSlide(currentSlide === 0 ? slides.length - 1 : currentSlide - 1);
  };

  const getPreviousIndex = () => {
    return currentSlide === 0 ? slides.length - 1 : currentSlide - 1;
  };

  const getNextIndex = () => {
    return currentSlide === slides.length - 1 ? 0 : currentSlide + 1;
  };

  return (
    <div className="slideshow-container">
      <div className="slides-wrapper">
        {/* Previous Slide */}
        <div className="slide side-slide prev-slide">
          <img src={slides[getPreviousIndex()]} alt="Previous" />
        </div>

        {/* Current Slide */}
        <div className="slide main-slide">
          <img src={slides[currentSlide]} alt="Current" />
        </div>

        {/* Next Slide */}
        <div className="slide side-slide next-slide">
          <img src={slides[getNextIndex()]} alt="Next" />
        </div>
      </div>

      <button className="prev" onClick={prevSlide}>&#10094;</button>
      <button className="next" onClick={nextSlide}>&#10095;</button>

      <div className="dots-container">
        {slides.map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentSlide ? 'active-dot' : ''}`}
            onClick={() => setCurrentSlide(index)}
          ></span>
        ))}
      </div>
    </div>
  );
}

export default Home;
